def get_codons(rna):
    codon_len = 3
    return [rna[i: i + codon_len] for i in range(0, len(rna), codon_len)]


def genetic_code(rna):
    # split section into codons
    codons = get_codons(rna)

    codon_aa_table = {"UUU": "F", "CUU": "L", "AUU": "I", "GUU": "V",
                      "UUC": "F", "CUC": "L", "AUC": "I", "GUC": "V",
                      "UUA": "L", "CUA": "L", "AUA": "I", "GUA": "V",
                      "UUG": "L", "CUG": "L", "AUG": "M", "GUG": "V",
                      "UCU": "S", "CCU": "P", "ACU": "T", "GCU": "A",
                      "UCC": "S", "CCC": "P", "ACC": "T", "GCC": "A",
                      "UCA": "S", "CCA": "P", "ACA": "T", "GCA": "A",
                      "UCG": "S", "CCG": "P", "ACG": "T", "GCG": "A",
                      "UAU": "Y", "CAU": "H", "AAU": "N", "GAU": "D",
                      "UAC": "Y", "CAC": "H", "AAC": "N", "GAC": "D",
                      "UAA": "STOP", "CAA": "Q", "AAA": "K", "GAA": "E",
                      "UAG": "STOP", "CAG": "Q", "AAG": "K", "GAG": "E",
                      "UGU": "C", "CGU": "R", "AGU": "S", "GGU": "G",
                      "UGC": "C", "CGC": "R", "AGC": "S", "GGC": "G",
                      "UGA": "STOP", "CGA": "R", "AGA": "R", "GGA": "G",
                      "UGG": "W", "CGG": "R", "AGG": "R", "GGG": "G"
                      }

    protein = [codon_aa_table.get(codon) for codon in codons]

    # get rid of an end indicator
    if protein[-1] == "STOP":
        protein.pop()
    return ''.join(protein)


if __name__ == '__main__':
    inputString = ""
    print(genetic_code(inputString))
