from Bio import pairwise2
from Bio.Align import substitution_matrices


if __name__ == '__main__':
    subs_mat = substitution_matrices.load('BLOSUM62')

    seq11 = "PLEASANTLY"
    seq12 = "MEANLY"

    seq1 = ""
    seq2 = ""
    alignments = pairwise2.align.localds(seq1, seq2, subs_mat, -11, -1)
    for alignment in alignments:
        seqA = alignment[0]
        seqB = alignment[1]
        score = alignment[2]
        start = alignment[3]
        end = alignment[4]
        seqA_local = seqA[start:end]
        seqB_local = seqB[start:end]

        seqA_local_no_gaps = seqA_local.replace('-', '')
        seqB_local_no_gaps = seqB_local.replace('-', '')

        print(int(score))
        print(seqA_local_no_gaps)
        print(seqB_local_no_gaps)
        print()

